import React, { useEffect } from "react";
import { Button, FormControl, InputGroup, ListGroup } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getCountriesAsync } from "../redux/countries/countriesSlice";

export default function SearchBar() {
  const dispatch = useDispatch();
  const [isFocused, setIsFocused] = useState(false)
  useEffect(() => {
    dispatch(getCountriesAsync());
  }, [dispatch]);

  const countries = useSelector((state) => state.countries.items);

  const focusHandler = () => {
    setIsFocused(true)
    console.log(isFocused)
  };

  return (
    <div style={{ marginTop: 20 }}>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Recipient's username"
          aria-label="Recipient's username"
          aria-describedby="basic-addon2"
          onFocus={focusHandler}
        />
        <Button variant="primary">ara</Button>{" "}
      </InputGroup>
      <ListGroup style={{marginBottom:"10px"}}>
      {countries? (
        <div style={{border:"1px solid black",maxHeight:"10rem", overflow:"auto"}}>
        {countries.map(country=>(<ListGroup.Item>{country.name}</ListGroup.Item>))}
        
        </div>
      ):''}
      </ListGroup>
      
    </div>
  );
}
