import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";


export const getUniversitiesAsync = createAsyncThunk(
  "universities/getUniversitiesAsync",
  async ()=>{
    const response = await axios.get("http://universities.hipolabs.com/search?country=Turkey");
    console.log("response")
    console.log(response)
    return await response.data
  }
)

export const universitiesSlice = createSlice({
  name:"universities",
  initialState:{
    items:[]
  },
  reducers:{
    add:(state,action) =>{
      state.items.push(action.payload)
    }
  },
  extraReducers:{
    //get universities
    [getUniversitiesAsync.fulfilled]:(state,action)=> {
      state.universities = action.payload;    //buradaki action response'dan dönen data ile ilgili
      console.log("action.payload")
      console.log(action.payload)

    }
  }
})

export const {} = universitiesSlice.actions
export default universitiesSlice.reducer
