import { createSlice } from "@reduxjs/toolkit";
import UniversityService from "../../service/UniversityService";

const universityService = new UniversityService()

export const universitiesSlice = createSlice({
    name:"universities",
    initialState:{
        universities:[],
    },
    reducers:{
        setUniversitiesByCountryName: (state,action)=>{
            universityService.getUniversitiesByCountry(action.payload).then(response=>response.data.map(university=>console.log(university)))
        }
    }
})

export const {setUniversitiesByCountryName} = universitiesSlice.actions
export default universitiesSlice.reducer
