import axios from "axios";
import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import { useSelector } from "react-redux";
import UniversityService from "../service/UniversityService";

export default function ListPage() {

    const universities = useSelector(state => state.universities.universities)
    console.log(universities)
    // const [universities, setUniversities] = useState([])
    // useEffect(() => {
    //     const universityService = new UniversityService()
        
    //     universityService.getUniversitiesByCountry("Turkey").then(response=>console.log(response.data))
    // }, [])

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>University Name</th>
            <th>Domain</th>
            <th>Web Page</th>
          </tr>
        </thead>
        <tbody>
      
        {universities.map(university=>(
            <tr key={university.index}>
        <td>{universities.indexOf(university)+1}</td>
        <td>{university.name}</td>
        <td>{university.domains[0]}</td>
        <td>
          <a
            style={{ textDecoration: "none" }}
            href={university.web_pages[0]}
            target="_blank"
          >
            {university.web_pages[0]}
          </a>
        </td>
      </tr>
        ))}
          
        </tbody>
      </Table>
    </div>
  );
}
