import React, { useEffect, useState } from "react";
import {
  Button,
  FormControl,
  Image,
  InputGroup,
  ListGroup,
} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getCountriesAsync } from "../redux/countries/countriesSlice";

export default function SearchBar() {
  const dispatch = useDispatch();
  const [isFocused, setIsFocused] = useState(false);
  const [enteredUniversity, setEnteredUniversity] = useState("")
  const [filteredCountries, setFilteredCountries] = useState([])
  useEffect(() => {
    dispatch(getCountriesAsync());
  }, [dispatch]);

  const countries = useSelector((state) => state.countries.items);

  const focusHandler = () => {
    setTimeout(() => {
      setIsFocused(true);
    }, 200);
  };
  const blurHandler = () => {
    setTimeout(() => {
      setIsFocused(false);
    }, 200);
    setEnteredUniversity('')
  };
  const inputChangeHandler =(e)=> {
    setEnteredUniversity(e.target.value)
  }

  return (
    <div style={{ marginTop: 20 }}>
      <InputGroup onChange={inputChangeHandler} className="mb-3">
        <FormControl
          placeholder="Recipient's username"
          value={enteredUniversity}
          aria-describedby="basic-addon2"
          onFocus={focusHandler}
          onBlur={blurHandler}
        />
        <Button variant="primary">ara</Button>{" "}
      </InputGroup>
      <ListGroup style={{ marginBottom: "10px" }}>
        {countries && isFocused ? (
          <div
            style={{
              border: "1px solid black",
              maxHeight: "10rem",
              overflow: "auto",
            }}
          >
            {!enteredUniversity? countries.map((country) => (
             
             <a href={"universities/"+country.name}> <ListGroup.Item  key={country.numericCode}>
              <Image
                  border="1px solid"
                  width="20rem"
                  src={country.flag}
                  rounded
                />{" "}
                {country.name}
                
              </ListGroup.Item></a>
            )):countries.filter(country=>(country.name).toLowerCase().includes(enteredUniversity.toLowerCase())).map(country=>(<a href={"universities/"+country.name}> <ListGroup.Item  key={country.numericCode}>
              <Image
                  border="1px solid"
                  width="20rem"
                  src={country.flag}
                  rounded
                />{" "}
                {country.name}
                
              </ListGroup.Item></a>))}
          </div>
        ) : (
          ""
        )}
      </ListGroup>
    </div>
  );
}
